import React, { useState } from "react";
import axios from "axios";
import "./Contact.css";

const Contact = () => {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    message: ""
  });
  const [status, setStatus] = useState("");

  const handleChange = (e) => {
    const { id, value } = e.target;
    setFormData({
      ...formData,
      [id]: value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      setStatus("Sending...");
      // API URL for the FastAPI backend
      const response = await axios.post("http://localhost:8000/api/send-mail/", formData, {
        headers: {
          "Content-Type": "application/json"
        }
      });
      setStatus(response.data.message || "Email sent successfully!");
      setFormData({
        name: "",
        email: "",
        message: ""
      });
    } catch (error) {
      setStatus("Error sending email. Please try again.");
    }
  };

  return (
    <div className="contact-container">
      <div className="contact-header">
        <div className="contact-button">
          <svg
            stroke="currentColor"
            fill="none"
            strokeWidth="2"
            viewBox="0 0 24 24"
            strokeLinecap="round"
            strokeLinejoin="round"
            className="contact-icon"
            height="20"
            width="20"
            xmlns="http://www.w3.org/2000/svg"
          >
            <path d="M22 16.92v3a2 2 0 0 1-2.18 2 19.79 19.79 0 0 1-8.63-3.07 19.5 19.5 0 0 1-6-6 19.79 19.79 0 0 1-3.07-8.67A2 2 0 0 1 4.11 2h3a2 2 0 0 1 2 1.72 12.84 12.84 0 0 0 .7 2.81 2 2 0 0 1-.45 2.11L8.09 9.91a16 16 0 0 0 6 6l1.27-1.27a2 2 0 0 1 2.11-.45 12.84 12.84 0 0 0 2.81.7A2 2 0 0 1 22 16.92z"></path>
          </svg>
          <span className="contact-text">Contact me</span>
        </div>
        <h1 className="contact-title">
          <span className="highlight">Contact</span>&nbsp;Me
        </h1>
        <div className="underline"></div>
      </div>
      <div className="contact-form-container">
        <h2 className="form-title">Send me a mail.</h2>
        <p className="form-subtitle">
          Once the form is submitted, the fields will be reset.
        </p>
        <form className="contact-form" onSubmit={handleSubmit}>
          <label htmlFor="name">Name</label>
          <input
            type="text"
            id="name"
            value={formData.name}
            onChange={handleChange}
            placeholder="Enter your name"
          />
          <label htmlFor="email">Email</label>
          <input
            type="email"
            id="email"
            value={formData.email}
            onChange={handleChange}
            placeholder="Enter your email"
          />
          <label htmlFor="message">Your Message</label>
          <textarea
            id="message"
            value={formData.message}
            onChange={handleChange}
            placeholder="Enter your message"
          ></textarea>
          <button type="submit" className="submit-button">Submit</button>
        </form>
        {status && <p className="status-message">{status}</p>}
      </div>
    </div>
  );
};

export default Contact;
