import React, { useState, useEffect } from "react";
import "./Home.css";
import {Lottie} from "lottie-react";
import animationData from "./Home.json";
import twitter from "../assets/social-media.png";
import instagram from "../assets/instagram.png";
import threads from "../assets/threads.png";
import linkedin from "../assets/linkedin.png";
import resume from "../assets/image.png";

function Home ()  {
  const [dynamicText, setDynamicText] = useState("Python");
  const [isAnimating, setIsAnimating] = useState(false);
  const [isMobile, setIsMobile] = useState(window.innerWidth <= 768);

  useEffect(() => {
    const handleResize = () => {
      setIsMobile(window.innerWidth <= 768);
    };

    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  useEffect(() => {
    const interval = setInterval(() => {
      setIsAnimating(true);
      setTimeout(() => {
        setDynamicText((prevText) =>
          prevText === "Python" ? "Frontend" : "Python"
        );
        setIsAnimating(false);
      }, 1000);
    }, 3000);

    return () => clearInterval(interval);
  }, []);

  return (
    <div className="container">
      <div className="content-wrapper">
        <h1 className="name">Jayeesh</h1>
        <h2 className="surname">Pandey</h2>
        <p className="description">
          I am a Passionate{" "}
          <strong
            className={`dynamic-text ${
              isAnimating ? "text-animate-out" : "text-animate-in"
            }`}
          >
            {dynamicText}
          </strong>
          <br />
          <strong className="developer-text">Developer.</strong>
        </p>
        <div className="social-icons">
          <a
            href="https://twitter.com"
            className="icon"
            target="_blank"
            rel="noopener noreferrer"
          >
            <img src={twitter} alt="Twitter" />
          </a>
          <a
            href="https://threads.com"
            className="icon"
            target="_blank"
            rel="noopener noreferrer"
          >
            <img src={threads} alt="Threads" />
          </a>
          <a
            href="https://instagram.com"
            className="icon"
            target="_blank"
            rel="noopener noreferrer"
          >
            <img src={instagram} alt="Instagram" />
          </a>
          <a
            href="https://linkedin.com"
            className="icon"
            target="_blank"
            rel="noopener noreferrer"
          >
            <img src={linkedin} alt="LinkedIn" />
          </a>
        </div>
        <a
          href="https://drive.google.com/file/d/1fmj1ecNT0HX2FAdrmdua1oBBJ19ofa-9/view?usp=drive_link"
          target="_blank"
          rel="noopener noreferrer"
          className="button"
          style={{
            display: "inline-flex",
            alignItems: "center",
            justifyContent: "center",
            padding: "12px 20px",
            fontSize: "16px",
            fontWeight: "bold",
            textDecoration: "none",
            backgroundColor: "#7e57c2",
            color: "#fff",
            borderRadius: "8px",
            marginTop: "20px",
          }}
        >
          <img
            src={resume}
            alt="Resume Icon"
            className="resume-icon"
            style={{ marginRight: "10px", width: "24px", height: "24px" }}
          />
          View Resume
        </a>
      </div>

      {!isMobile && (
        <div className="animation-container">
          <Lottie animationData={animationData} loop={true} />
        </div>
      )}
    </div>
  );
};

export default Home;
