import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { Home, User, Code, Mail, Menu, X } from 'lucide-react';
import './Navigation.css';

const Navigation = () => {
  const [isOpen, setIsOpen] = useState(false);

  return (
    <>
      {/* Desktop Navigation */}
      <nav className="desktop-nav navigation-container">
        <Link to="/" className="icon-wrapper" title="Home">
          <Home className="nav-icon" />
        </Link>
        <Link to="/about" className="icon-wrapper" title="About">
          <User className="nav-icon" />
        </Link>
        <Link to="/skills" className="icon-wrapper" title="Skills">
          <Code className="nav-icon" />
        </Link>
        <Link to="/contact" className="icon-wrapper" title="Contact">
          <Mail className="nav-icon" />
        </Link>
      </nav>

      {/* Mobile Navigation */}
      <div className="mobile-nav">
        <button
          className="hamburger mobile-nav-toggle"
          onClick={() => setIsOpen(!isOpen)}
          aria-expanded={isOpen}
        >
          {isOpen ? <X size={24} /> : <Menu size={24} />}
        </button>

        <nav className={`mobile-menu ${isOpen ? 'open' : ''}`}>
          <div className="mobile-nav-content">
            <Link to="/" onClick={() => setIsOpen(false)}>Home</Link>
            <Link to="/about" onClick={() => setIsOpen(false)}>About</Link>
            <Link to="/skills" onClick={() => setIsOpen(false)}>Skills</Link>
            <Link to="/contact" onClick={() => setIsOpen(false)}>Contact</Link>
          </div>
        </nav>
      </div>
    </>
  );
};

export default Navigation;
