import React, { useState, useEffect } from "react";
import "./About.css";
import Lottie from "lottie-react";
import animationData from "./About.json";

function About() {
  const [dynamicTitle, setDynamicTitle] = useState("Python");
  const [isMobile, setIsMobile] = useState(window.innerWidth <= 768);

  useEffect(() => {
    const handleResize = () => {
      setIsMobile(window.innerWidth <= 768);
    };

    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  useEffect(() => {
    const interval = setInterval(() => {
      setDynamicTitle((prevTitle) =>
        prevTitle === "Python" ? "Frontend" : "Python"
      );
    }, 3000);
    return () => clearInterval(interval);
  }, []);

  return (
    <div className="about-page">
      <div className="left-section">
        <div className="about-button">
          <svg
            stroke="currentColor"
            fill="none"
            strokeWidth="2"
            viewBox="0 0 24 24"
            strokeLinecap="round"
            strokeLinejoin="round"
            className="about-icon"
            height="20"
            width="20"
            xmlns="http://www.w3.org/2000/svg"
          >
            <path d="M19 21v-2a4 4 0 0 0-4-4H9a4 4 0 0 0-4 4v2"></path>
            <circle cx="12" cy="7" r="4"></circle>
          </svg>
          <span className="about-text">About me</span>
        </div>

        <h1 className="title">
          <span className="dynamic-title">{dynamicTitle}</span>{" "}
          <span className="highlight">Developer</span>
          <br />
          <span className="subtitle">Based In India</span>
        </h1>
        <div className="underline"></div>

        <div className="custom-box">
          <h1 className="custom-box-text">
            I am a {dynamicTitle} Developer from Pune, India, passionate about problem-solving and creating meaningful digital solutions.  With a solid foundation in Python, FastAPI, and ReactJS, I am dedicated to learning and exploring new technologies to enhance my skill set. I thrive on creating efficient, user-centric solutions while continuously adapting to emerging tools and frameworks.

With a strong passion for problem-solving and innovation, I excel at collaborating with cross-functional teams, managing dynamic challenges, and delivering impactful digital experiences. 
          </h1>
        </div>
      </div>

      {!isMobile && (
        <div className="right-section">
          <Lottie animationData={animationData} loop={true} />
        </div>
      )}
    </div>
  );
}
export default About;
